
public class Ejercicio_casting_double {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float num1;
		double num2 = 10.255684965676579;
		num1 = (float)num2;
		
		System.out.println("Valor del float: " + num1);
		System.out.println("Valor del double: " + num2);
	}
}
