
public class T3_Ejercicio5_pag11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub		
		for (int i=1; i<4; i++) {
			System.out.print(i);
			System.out.print(" ");
			for (int j=1; j>=1; j--)
			{	System.out.print(j);
				System.out.print(" ");
			}
			System.out.print("/n");
		}
	}
}